export default function App() {
  const [tab, setTab] = useState("deck");
  const [deckText, setDeckText] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  if (!user) {
    return <Login onLoginSuccess={setUser} />;
  }

  const tabs = [
    { id: "deck", label: "Deck Analyzer", icon: "üÉè" },
    { id: "hand", label: "Hand Analyzer", icon: "üé¥" },
    { id: "matchups", label: "Matchups", icon: "‚öîÔ∏è" },
    { id: "meta", label: "Meta Dashboard", icon: "üìä" },
  ];

  return (
    <div className="App">
      <header className="app-header">
        <h1>üÉè Lorcana AI</h1>
        <div className="user-info">
          <span>
            Ol√°, <strong>{user.username || user.email}</strong>
          </span>
          <button onClick={handleLogout} className="btn-logout">
            Sair
          </button>
        </div>
      </header>

      <div className="app-main">
        <div className="tabs">
          {tabs.map((t) => (
            <button
              key={t.id}
              className={`tab-btn ${tab === t.id ? "active" : ""}`}
              onClick={() => setTab(t.id)}
            >
              {t.icon} {t.label}
            </button>
          ))}
        </div>

        <div className="tab-content">
          {tab === "deck" && <DeckAnalyzer deckText={deckText} setDeckText={setDeckText} />}
          {tab === "hand" && <HandAnalyzerTab deckText={deckText} />}
          {tab === "matchups" && <MatchupsTab deckText={deckText} />}
          {tab === "meta" && <MetaDashboard />}
        </div>
      </div>
    </div>
  );
}
